      console.log("cleanup called");
    };
  }, []);
*/
  useEffect(() => {
    console.log("count is mounted",count);
  }, [count]);

  return (
    <div>
      <input type="text" onChange={(e) => setCount(Number(e.target.value))} />
    </div>
  );
};

export default UseEffectComponent;
